mapper.select:
select o.item_type itemType, o.item_weight itemWeight, o.item_price itemPrice, o.order_amount orderAmount, o.order_status orderStatus, o.order_code orderCode, o.seller_user_id sellerUserId, selluser.user_name sellerUserName, selluser.account_name sellerAccountName, o.buyer_user_id buyerUserId, buyuser.user_name buyerUserName, buyuser.account_name buyerAccountName from t_transaction_order o left join t_user buyuser on o.buyer_user_id = buyuser.user_id left join t_user selluser on o.seller_user_id = selluser.user_id where 1=1
and (o.order_status = 3 or o.order_status = 4)
and o.order_status = #{orderStatus}
and o.seller_user_id = #{creatorUserId}
and o.buyer_user_id = #{creatorUserId}
order by o.created_time desc
